    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        float nota1 = Float.parseFloat(txtnota1.getText()); 
        float nota2 = Float.parseFloat(txtnota2.getText()); 
        float nota3 = Float.parseFloat(txtnota3.getText()); 


        float resultado = (nota1 + nota2 + nota3)/3;
        String promedio;
        if(resultado>=10.5){
            promedio="aprobado";
        }else{
            promedio="desaprobado";
        }
        
        alumno alumno = new alumno(txtcodigo.getText(),txtnombre.getText(),txtcurso.getText(),promedio);
        lista.add(alumno);
        //limpiar 
        txtcodigo.setText("");
        txtnombre.setText("");
        txtcurso.setText("");
        txtnota1.setText("");
        txtnota2.setText("");
        txtnota3.setText("");
        
        mostrarDatos();
    }//GEN-LAST:event_jButton1ActionPerformed

    private void eliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_eliminarActionPerformed
    int eliminar=-1;
    for(int i=0;i<lista.size();i++){
        if(lista.get(i).getCodigo().equals(txtcodigo.getText())|| lista.get(i).getCodigo().equals(txtnombre.getText())){
            eliminar = i;
            break;
        }
        
    }
    if(eliminar>=0){
        lista.remove(eliminar);
        mostrarDatos();
        txtcodigo.setText("");
        txtnombre.setText("");
    }else{
        JOptionPane.showMessageDialog(null,"error de dato");
    }
    }//GEN-LAST:event_eliminarActionPerformed
public void mostrarDatos(){
    String matriz[][]= new String[lista.size()][4];
        for (int i=0; i<lista.size(); i++){
            matriz[i][0]=lista.get(i).getCodigo();
            matriz[i][1]=lista.get(i).getNombre();
            matriz[i][2]=lista.get(i).getCurso();
            matriz[i][3]=lista.get(i).getPromedio();
        }
        listaestudiantes.setModel(new javax.swing.table.DefaultTableModel(matriz,new String[]{"codigo","nombre","curso","CondiciÃ³n"}));
}
